import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";

const Register = () => {
  const [user, setUser] = useState({
    email: "",
    password: "",
    name: "",
    lastname: "",
  });
  const [loading, setLoading] = useState(false);
  const [editing, setEditing] = useState(false);

  const navigate = useNavigate();
  const params = useParams();

  useEffect(() => {
    if (params.id) {
      loadUser(params.id);
    }
  }, [params.id]);

  const loadUser = async (id) => {
    const res = await fetch("http://localhost:4000/register/" + id);
    const data = await res.json();
    setUser({ title: data.title, description: data.description });
    setEditing(true);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    setLoading(true);
    try {
      if (editing) {
        const response = await fetch(
          "http://localhost:4000/register/" + params.id,
          {
            method: "PUT",
            headers: { "Content-Type": "application/json" },
            body: JSON.stringify(user),
          }
        );
        await response.json();
        console.log(response);
      } else {
        const response = await fetch("http://localhost:4000/users", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify(user),
        });
        await response.json();
        console.log(response);
      }

      setLoading(false);
      navigate("/");
    } catch (error) {
      console.error(error);
    }
  };

  const handleChange = (e) =>
    setUser({ ...user, [e.target.name]: e.target.value });

  return (
    <div classNameName="container">
      <form className="row g-3" onSubmit={handleSubmit}>
        <div className="col-6">
          <label className="col-form-label">Email</label>
          <input
            type="email"
            className="form-control"
            id="inputEmail4"
            onChange={handleChange}
          />
        </div>
        <div className="col-6">
          <label classNameName="col-form-label">Password</label>
          <input
            type="password"
            className="form-control"
            id="inputPassword4"
            onChange={handleChange}
          />
        </div>
        <div className="col-6">
          <label className="col-form-label">Name</label>
          <input type="text" className="form-control" onChange={handleChange} />
        </div>
        <div className="col-6">
          <label className="col-form-label">Lastname</label>
          <input type="text" className="form-control" onChange={handleChange} />
        </div>
      </form>
      <br />
      <button type="submit" className="btn btn-primary">
        Sign in
      </button>
    </div>
  );
};

export default Register;
