import { useState } from "react";
import { NavLink, useNavigate } from "react-router-dom";
import axios from "axios";

const Navbar = () => {
  const API_URL_SEARCH = `https://api.themoviedb.org/3/search/movie?api_key=e30a9a93aa4cdbed38f373e13824625b&query=${query}`;
  const navigate = useNavigate();
  const [query, setQuery] = useState("");

  const searchMovies = async () => {
    const {
      data: { results },
    } = await axios.get(API_URL_SEARCH);
    console.log(results);
    setMovies(results);
  };

  const handleClick = () => {
    navigate("/login");
  };
  const changeHandler = (e) => {
    setQuery(e.target.value);
  };

  return (
    <nav className="navbar navbar-expand-lg navbar-dark bg-dark fixed-top mb-5">
      <span class="navbar-brand mb-0 h1">Movies DB</span>

      <div className="container-fluid">
        <ul className="navbar-nav me-auto mb-2 mb-lg-0">
          <li className="nav-item">
            <NavLink to="/" className="nav-link active">
              Home
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink to="/register" className="nav-link active">
              Register
            </NavLink>
          </li>
          <li className="nav-item">
            <NavLink to="/series" className="nav-link active">
              Series
            </NavLink>
          </li>
        </ul>
        <div>
          <form className="container mb-5 mt-5" onSubmit={searchMovies}>
            <input
              className="mt-4"
              type="text"
              placeholder="search"
              onChange={changeHandler}
            />
            <button className="btn btn-warning">Search</button>
          </form>
        </div>
        <div className=" justify-content-end">
          <button
            onClick={handleClick}
            className="btn btn-outline-success me-2"
          >
            Login
          </button>
        </div>
      </div>
    </nav>
  );
};

export default Navbar;
