import axios from "axios";
import { useState, useEffect } from "react";
import { Modal, show, Button } from "react-bootstrap";

const HomePage = () => {
  const [movies, setMovies] = useState([]);
  const [query, setQuery] = useState("");
  const [show, setShow] = useState(false);

  const API_URL = "https://api.themoviedb.org/3/movie/popular";
  const API_URL_SEARCH = `https://api.themoviedb.org/3/search/movie?api_key=e30a9a93aa4cdbed38f373e13824625b&query=${query}`;

  //función para traer los datos de la API
  const fetchMovies = async () => {
    const {
      data: { results },
    } = await axios.get(API_URL, {
      params: {
        api_key: "e30a9a93aa4cdbed38f373e13824625b",
      },
    });
    console.log(results);
    setMovies(results);
  };

  useEffect(() => {
    fetchMovies();
  }, []);

  //función para buscar películas
  const searchMovies = async (e) => {
    e.preventDefault();

    try {
      const res = await fetch(API_URL_SEARCH);
      const data = await res.json();
      console.log(data);
      setMovies(data.results);
    } catch (e) {
      console.log(e);
    }
  };
  const handleChange = (e) => {
    setQuery(e.target.value);
  };
  const handleShow = () => setShow(true);
  const handleClose = () => setShow(false);

  return (
    <>
      <form className="container mb-5 mt-5" onSubmit={searchMovies}>
        <input
          className="mt-4"
          type="text"
          placeholder="search"
          onChange={handleChange}
        />
        <button className="btn btn-warning">Search</button>
      </form>
      <div className="container text-center mt-5">
        <div className="row">
          {movies.map((movie) => (
            <div key={movie.id} className="col-sm-4">
              <img
                src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                height={600}
                width="100%"
              />
              <h4>{movie.title}</h4>
              <button
                type="button"
                className="btn btn-dark"
                onClick={handleShow}
              >
                View More
              </button>
              <Modal show={show} onHide={handleClose}>
                <Modal.Header closeButton>
                  <Modal.Title></Modal.Title>
                </Modal.Header>
                <Modal.Body>
                  <img
                    className="card-img-top"
                    style={{ width: "14rem" }}
                    src={`https://image.tmdb.org/t/p/w500/${movie.poster_path}`}
                  />
                  <h3>{movie.title}</h3>
                  <h4>IMDb: {movie.vote_average}</h4>

                  <br></br>
                  <h6>Overview</h6>
                  <p>{movie.overview}</p>
                </Modal.Body>
                <Modal.Footer>
                  <Button variant="secondary" onClick={handleClose}>
                    Close
                  </Button>
                </Modal.Footer>
              </Modal>
            </div>
          ))}
        </div>
      </div>
    </>
  );
};

export default HomePage;
